package problems;


import org.testng.annotations.Test;
import static org.assertj.core.api.Assertions.assertThat;


@Test
public class GreatestCommonDivisorTest {

    public void checkGCDStrings() {
        // given
        GreatestCommonDivisor gcd = new GreatestCommonDivisor();

        // when
        String s1 = gcd.gcdOfStrings("ABCABC", "ABC");
        String s11 = gcd.gcdOfStrings("ABCABC", "ABCABC");
        String s2 = gcd.gcdOfStrings("ABABAB", "ABAB");
        String s3 = gcd.gcdOfStrings("ABCDEF", "ABC");
        String s4 = gcd.gcdOfStrings("TAUXXTAUXXTAUXXTAUXXTAUXX", "TAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXXTAUXX");
        String s5 = gcd.gcdOfStrings("AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA", "AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA");

        /* 360, 640*/
        String s6 = gcd.gcdOfStrings("",
                "");

        // then
        assertThat(s1).isEqualTo("ABC");
        assertThat(s11).isEqualTo("ABCABC");
        assertThat(s2).isEqualTo("AB");
        assertThat(s3).isEqualTo("");
        assertThat(s4).isEqualTo("TAUXX");
        assertThat(s5).isEqualTo("A");
        assertThat(s6).isEqualTo("AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA");
    }
}
